package dev.dolu.userservice.controller;import dev.dolu.userservice.models.User;import dev.dolu.userservice.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.UUID;@RestController@RequestMapping("/api/admin")public class AdminController {    private final UserService userService;    @Autowired    public AdminController(UserService userService) {        this.userService = userService;    }    @GetMapping("/{id}")    public ResponseEntity<?> findUserById(@PathVariable UUID id) {        User user = userService.findUserById(id);        if (user == null) {            return new ResponseEntity<>("User not found", HttpStatus.NOT_FOUND);        }        return new ResponseEntity<>(user, HttpStatus.OK);    }}